import React, { useEffect, useState } from "react";

import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';

import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';

const CustomTableRow = withStyles((theme) => ({
    root: {

            backgroundColor: 'black',

    },
}))(TableRow);

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '5rem',
        width: '100%',
    },
    container: {
        maxHeight: 540,
    },
    button: {
        background: theme.palette.grey[300],
        padding: ".4rem",
        "margin-left": ".2rem",
        "margin-right": ".2rem",
    },
}));

const EmployList = () => {  
    const classes = useStyles();
    const [employees, setEmployees] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(3);
    const [totalItems, setTotalItems] = useState(1);

    useEffect( () => {
        getEmploees(page+1, rowsPerPage);
    }, [page, rowsPerPage]); 

    const getEmploees = async(page,size)=>   {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(`http://localhost:5000/api/employee/all?page=${page}&size=${size}`, requestOptions)
            .then(response => response.json())
            .then(data => (setEmployees(data.items), setTotalItems(data.totalItems)));
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <div>
            {employees === undefined ? <div>lol</div> :
            <Paper className={classes.root}>          
            <TableContainer className={classes.container}>
                <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <CustomTableRow>
                            <TableCell>
                                #
                            </TableCell>
                            <TableCell>
                                ID
                            </TableCell>
                            <TableCell>
                                Name
                            </TableCell>
                            <TableCell>
                                Surname
                            </TableCell>
                            <TableCell>
                                Email
                            </TableCell>
                            <TableCell>
                                Position
                            </TableCell>
                            <TableCell>
                                Department
                            </TableCell>                        
                            <TableCell align="center">
                                Action
                            </TableCell>
                        </CustomTableRow>
                    </TableHead>
                    <TableBody >
                        {employees.map((employee,index) => (
                            <TableRow hover key={employee.id}>
                            <TableCell>
                                {index}
                            </TableCell>
                            <TableCell>
                                {employee.id}
                            </TableCell>
                            <TableCell>
                                {employee.employeeData.name}
                            </TableCell>
                            <TableCell>
                                {employee.employeeData.surname}
                            </TableCell>
                            <TableCell>
                                {employee.employeeData.email}
                            </TableCell>
                            <TableCell>
                                {employee.position.name}
                            </TableCell>
                            <TableCell>
                                {employee.department.name}
                            </TableCell>
                                <TableCell align="center">
                                <IconButton className={classes.button} size="small" aria-label="edit">
                                    <VisibilityIcon/>
                                </IconButton>
                                <IconButton className={classes.button} size="small" aria-label="edit">
                                    <EditIcon/>
                                </IconButton>
                                <IconButton className={classes.button}  size="small" aria-label="del">
                                    <DeleteIcon/>
                                </IconButton>                                
                            </TableCell>
                        </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[3, 10, 25, 35]}
                component="div"
                count={totalItems}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
            />
        </Paper>
        }
    </div>
    );
}
export default EmployList;
