import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField, DarkSelect, DarkChipList } from '../GlobalComponents';
import APIURL from '../../Services/Globals';
import AddTeamMembers from './AddTeamMembers';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1rem",
        background: theme.palette.grey[400],
        paddingBottom: '0px',
        boxShadow: theme.shadows[2]
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    topBox: {
        marginTop: '0.1px',
        marginBottom: "15px",
        background: theme.palette.grey[800],
        padding: '8px',
        boxShadow: theme.shadows[2]
    }
}));

const CreateTeam = () => {
    const classes = useStyles();
    const [team, setTeam] = useState({ name: '' });



    const onTeamLeaderSelected = (leader) => {
        setTeam({ ...team, teamLeader: leader })
    }
    const onTeamMembersSelected = (members) => {
        setTeam({ ...team, members: members })
    }

    return (
        <div>
            <div className={classes.mainConteiner}>
                <div boxshadow={2} className={classes.title}>
                    <h3 >Create New Team</h3>
                </div>
                <div className={classes.topBox}>
                    <DarkTextField
                        label="Team Name"
                        name="teamName"
                        onChange={(e) => { setTeam({ ...team, name: e.target.value }) }}
                    />
                </div>


                <AddTeamMembers onSelectionConfirm={onTeamMembersSelected} />
            </div>
        </div>
    )
}
export default CreateTeam;
