import APIURL from './Globals'
import axios from "axios";


export const UserAccess = (permissions) => {
    if (permissions.length < 1) {
        return;
    }
    const isUsserPermit = (premName) => {
        let isPermit = false;
        permissions.forEach(element => {
            if (element.name === premName) {
                console.log(element.name);
                isPermit = true;
                return;
            }
        });
        return isPermit;
    }

    const allowedViews = {
        humanResources: isUsserPermit("Human-Resources"),
        admin: isUsserPermit('Admin'),
        teamManager: isUsserPermit('Team-Manager')
    };

    return allowedViews;
}

export const AllPermissions = async () => {
    const res = await axios.get(APIURL + 'permissions')
    return res.data[1];
};

export default {
    AllPermissions,
    UserAccess
};
