import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField, DarkSelect, DarkChipList } from '../GlobalComponents';
import APIURL from '../../Services/Globals';

import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import Divider from '@material-ui/core/Divider';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {

    },
    listComponent: {
        margin: '8px',
        width: '100%',
        minHeight: '700px',
        maxHeight: '700px',
        paddingTop: 0,
        backgroundColor: theme.palette.background.paper,
        color: 'white',
        boxShadow: theme.shadows[2],
        borderRadius: '4px',
        display: "flex",
        flexDirection: 'column'
    },
}));

const HR_Manager = () => {
    const classes = useStyles();
    const [positions, setPositions] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [permissions, setPermissions] = useState([])


    useEffect(() => {
        getData();
    }, []);

    const getData = async () => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(APIURL + `departments`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setDepartments(data));

        await fetch(APIURL + `positions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPositions(data));

        await fetch(APIURL + `permissions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPermissions(data));
    }
    return (
        <div className={classes.mainConteiner}>
            {positions.length <= 0 ? null :// -------------------------------------------->Positions List
                <div style={{ overflow: 'auto', }}>
                    {positions.map((position, index) => (
                        <div key={"position" + position.id}>
                            <ListItem>
                                <ListItemText
                                    primary={"ID: " + position.id + " Name: " + position.name}
                                />
                                <ListItemSecondaryAction>
                                    <IconButton edge="end" onClick={console.log('edit')}>
                                        <EditIcon />
                                    </IconButton>
                                    <IconButton edge="end" onClick={console.log('del')}>
                                        <DeleteIcon />
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                            <Divider variant="inset" style={{ width: "100%", margin: "0" }} />
                        </div>
                    ))}
                </div>
            }
        </div>
    )
};
export default HR_Manager;
