import APIURL from './Globals'
import axios from "axios";


export const UserAccess = (permissions) => {
    if (permissions.length < 1) {
        return;
    }

    const isUsserPremit = (premName) => {
        permissions.forEach(element => {
            if (element.name === premName) {
                return true;
            }
        });
        return false;
    }

    const allowedViews = {
        humanResources: isUsserPremit('Human-Resources'),
        admin: isUsserPremit('Admin')
    };

    return allowedViews;
}

export const AllPermissions = async () => {
    const res = await axios.get(APIURL + 'permissions')
    return res.data[1];
};

export default {
    AllPermissions,
    UserAccess
};
