import React, { useEffect, useState } from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import {APIURL, DarkTextField, DarkSelect, DarkChipList} from './GlobalComponents';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.5rem",  
        background: theme.palette.grey[800], 
        padding:"1rem"
    },
}));

const CreateEmploy = () => {
    const classes = useStyles();
    const [employee, setEmployee] = useState({
        id: 0,
        employeeData: {
            name: "",
            surname: "",
            phoneNumber: "",
            email: "",
            employeeAddress: {
                id: 0,
                city: "",
                postCode: "",
                street:""
            }
        },
        position: {
            id: 0,
            name:"",
        },
        department: {
            id: 0,
            name: "",
        },
        employeePermissions: [],
        employmentDate: "",
        remoteWork: false
    });
    const [departments, setDepartments] = useState([]);
    const [positions, setPositions] = useState([]); 
    const [permissions, setPermissions] = useState([]); 
    const [employeePermissions, setEmployeePermissions] = useState([]); 

    useEffect(() => {
    getEmployeeProps()
    }, []);

    const PostEmployee = (formData) => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: formData
        };
        fetch(APIURL +'employee/create', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    const getEmployeeProps = async () => {
        const requestOptions = {
            method: 'Get',
        };
        await fetch(APIURL + `departments`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setDepartments(data));

        await fetch(APIURL + `positions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPositions(data));
        
        await fetch(APIURL + `permissions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPermissions(data));
    }

    const headleFormChange = event => {
        if (event.target.name === "permissionSelect") {
            if (employeePermissions.length === 0) {
                setEmployeePermissions([...employeePermissions, { id: event.target.value.id, name: event.target.value.name }]);           
            } else {
                let isPicked = false;
                employeePermissions.forEach(element => {
                    if (element.id === event.target.value.id) {
                        isPicked = true;
                    }
                });
                if (!isPicked) {
                    setEmployeePermissions([...employeePermissions, { id: event.target.value.id, name: event.target.value.name }]);
                }
            } 
            setEmployee(employee,{employeePermissions : employeePermissions})
        }
    }

    const hendlePermissionDelete = chipToDelete => {
        setEmployeePermissions(chips => chips.filter(chip => chip.name !== chipToDelete.name));
    }

    const hendlePostEmployee = () => {
        console.log(employee);
        PostEmployee(employee);
              
    }

    return (
        <div className={classes.mainConteiner}>          
            <form className={classes.root} noValidate autoComplete="off">
            <DarkTextField
                label="Name"
                name="employeeName"
                onChange={headleFormChange}
            />
            <DarkTextField
                label="Surname"
                name="employeeSurname"
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Department"
                name="departmentSelect"
                collection={departments}     
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Position"
                name="positionSelect"
                collection={positions}
                onChange={headleFormChange}
            />
            <DarkChipList
                label="Permissions"
                name="permissionSelect"
                collection={permissions}
                clipListCollection={employeePermissions}
                onChange={headleFormChange}
                onDelete={hendlePermissionDelete}
            />
            <button onClick={()=>hendlePostEmployee()}>ht</button>
            </form>
        </div>
    );
}
export default CreateEmploy;
