import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField, DarkSelect } from '../GlobalComponents';
import APIURL from '../../Services/Globals'
import AuthService, { getCurrentUser } from '../../Services/AuthService';

import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';

function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.8rem",
        background: theme.palette.grey[800],
        paddingBottom: '20px'
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    profileForm: {
        display: "flex",
        flexDirection: "column",
        padding: '8px'
    },
    usernameBox: {
        display: "flex",
        flexDirection: "row",
        '& .MuiFormControl-root': {
            width: '100%',
        }
    },
    formText: {
        color: 'white',
        margin: '20px 18px 6px'
    },
    formButton: {
        alignSelf: "flex-start",
        padding: '8px 25px',
        marginTop: '12px',
        marginLeft: '18px',
        marginRight: '10px'
    }
}));

const Profile = () => {
    const classes = useStyles();
    const [user, setUser] = useState(getCurrentUser().userDetails);
    const [userCredentials, setUserCredentials] = useState({
        isChangePassword: false,
        isUsernameChanged: false,
        username: '',
        oldPass: '',
        newPass: ''
    });

    const [allertProps, setAllertProps] = useState({
        text: '',
        open: false,
        type: 'success'
    });

    const onUsernameConfirm = () => {
        if (userCredentials.isUsernameChanged) {
            putUser(userCredentials.username);
        }
    }

    const onPasswordConfirm = () => {
        if (userCredentials.isChangePassword) {
            changePassword();
        }
    }

    const putUser = (newUsername) => {
        const requestOptions = {
            method: 'Put',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                id: user.id,
                username: newUsername === undefined ?
                    user.username : newUsername,
                password: user.password,
                employeeDTO: user.employeeDTO
            }),
        };
        fetch(APIURL + 'users/' + user.id, requestOptions)
            .then((res) => {
                if (res.status >= 400) {
                    console.log(res)
                    setAllertProps({
                        text: "Error",
                        open: true,
                        type: "error"
                    })
                    return;
                }
                setAllertProps({
                    text: newUsername === undefined ?
                        "User Data Changed" : "Username Changed",
                    open: true,
                    type: "success"
                });
                AuthService.login({
                    username: newUsername,
                    password: user.password
                }).then((data) => {
                    setUserCredentials({
                        ...userCredentials,
                        isUsernameChanged: false,
                        username: '',
                    });
                    setUser(getCurrentUser().userDetails)
                }, e => { console.log("login error") });
            });
    }

    const changePassword = () => {
        if (userCredentials.oldPass === '' &&
            userCredentials.newPass === '') {
            setUserCredentials({
                ...userCredentials,
                isChangePassword: false,
            })
            return;
        }
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                userId: user.id,
                oldPassword: userCredentials.oldPass,
                newPassword: userCredentials.newPass
            }),
        };
        fetch(APIURL + 'users/change-pass', requestOptions)
            .then((res) => {
                if (res.status >= 400) {
                    console.log(res)
                    setAllertProps({
                        text: "Error",
                        open: true,
                        type: "error"
                    })
                    return;
                }
                setAllertProps({
                    text: "Password Changed",
                    open: true,
                    type: "success"
                });
            }).then(() => {
                AuthService.login({
                    username: user.username,
                    password: userCredentials.newPass
                }).then((data) => {
                    setUserCredentials({
                        ...userCredentials,
                        isChangePassword: false,
                        oldPass: '',
                        newPass: ''
                    });
                    setUser(getCurrentUser().userDetails)
                }, e => { console.log("login error") });
            });
    }

    const onUsernameChange = (e) => {
        const usernameVal = e.target.value

        if (usernameVal === user.username) {
            setUserCredentials({
                ...userCredentials,
                isUsernameChanged: false,
            })

            return;
        }

        setUserCredentials({
            ...userCredentials,
            username: usernameVal,
            isUsernameChanged: true,
        })
    }

    const handleAllertClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setAllertProps({ ...allertProps, open: false });
    };

    return (
        <div className={classes.mainConteiner}>
            <Snackbar open={allertProps.open} autoHideDuration={4000} onClose={handleAllertClose}>
                <Alert onClose={handleAllertClose} severity={allertProps.type}>
                    {allertProps.text}
                </Alert>
            </Snackbar>
            <div boxshadow={2} className={classes.title}>
                <h3 >User Credentials</h3>
            </div>
            <form noValidate autoComplete="off">
                <div className={classes.profileForm}>
                    <div className={classes.usernameBox}>
                        <DarkTextField
                            label="Username"
                            defaultValue={user.username}
                            onChange={onUsernameChange}
                        />
                        {!userCredentials.isUsernameChanged ? null :
                            <Button
                                className={classes.formButton}
                                variant="contained"
                                color="primary"
                                onClick={onUsernameConfirm}
                            >Confirm</Button>
                        }
                    </div>
                    <h3 className={classes.formText}>Password:</h3>
                    {!userCredentials.isChangePassword ?
                        <Button
                            className={classes.formButton}
                            variant="contained"
                            color="primary"
                            onClick={() => setUserCredentials({ ...userCredentials, isChangePassword: true })}
                        >Change Password</Button> :
                        <React.Fragment>
                            <DarkTextField
                                label="Old Password"
                                type={'password'}
                                value={userCredentials.oldPass}
                                onChange={(e) =>
                                    setUserCredentials({
                                        ...userCredentials,
                                        oldPass: e.target.value,
                                        isChangePassword: true
                                    })
                                }
                            />
                            <DarkTextField
                                label="New Password"
                                type={'password'}
                                value={userCredentials.newPass}
                                onChange={(e) =>
                                    setUserCredentials({
                                        ...userCredentials,
                                        newPass: e.target.value,
                                        isChangePassword: true
                                    })
                                }
                            />
                        </React.Fragment>
                    }
                    {!userCredentials.isChangePassword ? null :
                        <Button
                            className={classes.formButton}
                            variant="contained"
                            color="primary"
                            onClick={onPasswordConfirm}
                        >Confirm</Button>
                    }

                </div>
            </form>
        </div>
    );
}
export default Profile;
