import React, { useEffect, useState } from "react";
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Grid from '@material-ui/core/Grid';
import CircularProgress from '@material-ui/core/CircularProgress';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';

import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';

import CloseIcon from '@material-ui/icons/Close';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import VisibilityIcon from '@material-ui/icons/Visibility';


function CircularProgressWithLabel(props) {
    const classes = useStyles();
    return (
        <Box className={classes.progressBox} position="relative" display="inline-flex">
            <CircularProgress className={classes.progressBox} variant="determinate" {...props} />
            <Box 
                top={0}
                left={0}
                bottom={0}
                right={0}
                position="absolute"
                display="flex"
                alignItems="center"
                justifyContent="center"
            >
                <Typography variant="h5" component="div" color="white">{`${Math.round(
                    props.value,
                )}%`}</Typography>
            </Box>
        </Box>
    );
}

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
    },
    body: {
        backgroundColor: theme.palette.grey[400],
    },
}))(TableCell)

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '1.5rem',
        width: '100%',
        background: theme.palette.grey[800],
        color:"white",
    },
    remoteWorkBox: {
        marginTop: '1.5rem',
        width: "308px",  
        padding:"10px",
        background: theme.palette.grey[800],
        color: "white",
    },
    statsBox: {
        marginTop: '1.5rem',
        marginLeft: '1.5rem',
        width: "115px",
        padding: "12px",
        position: "relative",
        background: theme.palette.grey[800],
        color: "white",
    },
    progressBox: {
        width: "100% !important",
        height: "100% !important",
        marginLeft: "4px"
    },
    container: {
        maxHeight: 540,
    },
    button: {
        background: theme.palette.grey[300],
        padding: ".4rem",
        "margin-left": ".2rem",
        "margin-right": ".2rem",
    },
    cardButton: {
       justifySelf:"flex-end"
    },
}));

const Home = () => {
    const classes = useStyles();
    const [employees, setEmployees] = useState([]);
    const [progress, setProgress] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgress((prevProgress) => (prevProgress === 90 ? 90 : prevProgress + 1));
        }, 45);
        return () => {
            clearInterval(timer);
        };
    }, []);

    useEffect(() => {
        getEmploees(1, 5);
    }, []);

    const getEmploees = async (page, size) => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(`http://localhost:5000/api/employee/all?page=${page}&size=${size}&order=date`, requestOptions)
            .then(response => response.json())
            .then(data => (setEmployees(data.items)));
    }

    return (
        <div>
            <Grid container item >
                    <Card className={classes.remoteWorkBox}>
                        <Grid container spacing={4}>
                            <Grid item direction="column" xs={7} >
                                <Typography noWrap variant="h5">123 Employees</Typography>                             
                                <Typography noWrap variant="h7">Work Rremotely</Typography>
                            </Grid>
                            <Grid item  xs={5}>
                                <CircularProgressWithLabel value={progress}/>
                            </Grid>
                        </Grid>
                    </Card>
                    <Card className={classes.statsBox}>
                        <Typography noWrap variant="h5">123</Typography>
                    <Typography noWrap variant="h7">Total Employees</Typography>
                    <CardActions >
                        <IconButton className={classes.cardButton} size="small" aria-label="view">
                            <VisibilityIcon />
                        </IconButton>
                    </CardActions>                       
                    </Card>
                  
                    <Card className={classes.statsBox}>
                        <Typography noWrap variant="h5">123</Typography>
                        <Typography noWrap variant="h7">Job Applicatios</Typography>
                    </Card>
            </Grid>
            {employees === undefined ? <div></div> :
                <Card className={classes.root}>
                        <CardHeader
                        title="Newly Hired Employees:"
                        />

                    <TableContainer className={classes.container}>
                        <Table stickyHeader aria-label="sticky table" >
                            <TableHead>
                                <TableRow >
                                    <StyledTableCell align="center">
                                        #
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        ID
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Name
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Surname
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Email
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Position
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Department
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Remote Work
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Employment Date
                                    </StyledTableCell>
                                    <StyledTableCell>
                                    </StyledTableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody >
                                {employees.map((employee, index) => (
                                    <TableRow hover key={employee.id}>
                                        <StyledTableCell align="center">
                                            {index + 1}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.id}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.employeeData.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.employeeData.surname}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.employeeData.email}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.position.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.department.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.remoteWork === true ?
                                                <CheckCircleIcon /> : <CloseIcon />}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.employmentDate.toString().split('T')[0]}
                                        </StyledTableCell>   
                                        <StyledTableCell align="center">
                                            <IconButton className={classes.button} size="small" aria-label="view">
                                                <VisibilityIcon />
                                            </IconButton>
                                        </StyledTableCell>                                               
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Card>
            }
        </div>
    );
}
export default Home;
