import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { useHistory } from "react-router-dom";
import { DarkTextField } from '../GlobalComponents';
import AuthService from '../../Services/AuthService'
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        margin: "0 auto",
        marginTop: "38px",
        width: "32rem",
        background: theme.palette.grey[800],
        paddingBottom: '25px',
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    formGrid: {
        margin: "20px",
        display: "grid",
        gridGap: "1.2rem",
    },
    createButton: {
        margin: '8px auto',
        fontSize: '16px',
        fontWeight: 550,
        display: 'block',
        width: "22rem",
    }
}));

const LoginUser = () => {
    const classes = useStyles();
    const [loginData, setLoginData] = useState({
        username: "",
        password: "",
    });
    const history = useHistory();

    const headleFormChange = e => {
        setLoginData({
            username: e.target.name === "username" ? e.target.value : loginData.username,
            password: e.target.name === "password" ? e.target.value : loginData.password
        })
    }

    const hendleLogin = (ev) => {
        AuthService.login(loginData).then((data) => {
            ev.preventDefault()
            history.push("/main/tasks-columns");
        }, e => { console.log("login error") });
    }

    return (
        <div className={classes.mainConteiner}>
            <div boxshadow={2} className={classes.title}>``
                <h2>User Credentials:</h2>
            </div>
            <form onSubmit={(e) => hendleLogin(e)} className={classes.root} noValidate autoComplete="off">
                <div className={classes.formGrid}>
                    <DarkTextField
                        label="Username"
                        name="username"
                        onChange={headleFormChange}
                    />
                    <DarkTextField
                        label="Password"
                        name="password"
                        type="password"
                        onChange={headleFormChange}
                    />
                </div>
                <Button
                    className={classes.createButton}
                    variant="contained"
                    color="primary"
                    type="submit"
                >Login</Button>
            </form>
        </div>
    );
}
export default LoginUser;
