import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField } from './GlobalComponents';
import AuthService from '../Services/AuthService'
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        margin: "4rem",
        background: theme.palette.grey[800],
        paddingBottom: '20px'
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    formGrid: {
        display: "grid",
        gridTemplateColumns: "1fr 1fr 1fr",
        justifyContent: "space-between",
        alignContent: "space-between",
        gridGap: "1.2rem 1.2rem",
    },
    createButton: {
        margin: '0 auto',
        display: 'block',
        width: "19rem",
    }
}));

const LoginUser = () => {
    const classes = useStyles();
    const [loginData, setLoginData] = useState({
        username: "",
        password: "",
    });
    const history = useHistory();

    const headleFormChange = e => {
        setLoginData({
            username: e.target.name === "username" ? e.target.value : loginData.username,
            password: e.target.name === "password" ? e.target.value : loginData.password
        })
    }

    const hendleLogin = () => {
        AuthService.login(loginData);
        history.push("/login");
    }

    return (
        <div className={classes.mainConteiner}>
            <div boxShadow={2} className={classes.title}>
                <h3 >Create new employee</h3>
            </div>
            <form className={classes.root} noValidate autoComplete="off">
                <div className={classes.formGrid}>
                    <DarkTextField
                        label="Username"
                        name="username"
                        onChange={headleFormChange}
                    />
                    <DarkTextField
                        label="Password"
                        name="password"
                        onChange={headleFormChange}
                    />
                </div>
                <Button
                    className={classes.createButton}
                    variant="contained"
                    color="primary"
                    onClick={hendleLogin}
                >Login</Button>
            </form>
        </div>
    );
}
export default LoginUser;
