import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { APIURL, DarkTextField, DarkSelect, DarkChipList } from './GlobalComponents';

import Button from '@material-ui/core/Button';
import { LocalLaundryService } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.5rem",  
        background: theme.palette.grey[800], 
        padding:"1rem"
    },
}));

const CreateEmploy = () => {
    const classes = useStyles();
    const [employee, setEmployee] = useState({
        id: 0,
        employeeData: {
            name: "",
            surname: "",
            phoneNumber: "",
            email: "",
            employeeAddress: {
                id: 0,
                city: "",
                postCode: "",
                street:""
            }
        },
        position: {
            id: 0,
            name:"",
        },
        department: {
            id: 0,
            name: "",
        },
        employeePermissions: [],
        employmentDate: "",
        remoteWork: false
    });
    const [departments, setDepartments] = useState([]);
    const [positions, setPositions] = useState([]); 
    const [allPermissions, setAllPermissions] = useState([]); 
    const [employeePermissions, setEmployeePermissions] = useState([]); 

    function solution(number) {
        let sum = 0;
        for (let i = 0; i < number; i++) {
            if ((3 % i) + (5 % i) === 0) {
                console.log(i);
                sum = sum + i;
            }
        }
        return sum;
    }

    useEffect(() => {
        console.log("sum= "+ solution(10));
    getEmployeeProps()
    }, []);

    const PostEmployee = (formData) => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: formData,
        };
        fetch(APIURL +'employee/create', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    const getEmployeeProps = async () => {
        const requestOptions = {
            method: 'Get',
        };
        await fetch(APIURL + `departments`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setDepartments(data));

        await fetch(APIURL + `positions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPositions(data));
        
        await fetch(APIURL + `permissions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setAllPermissions(data));
    }

    const headleFormChange = e => {
        if (e.target.name === "permissionSelect") {
            if (employeePermissions.length === 0) {
                setEmployeePermissions([...employeePermissions, { id: e.target.value.id, name: e.target.value.name }]);           
            } else {
                let isPicked = false;
                employeePermissions.forEach(element => {
                    if (element.id === e.target.value.id) {
                        isPicked = true;
                    }
                });
                if (!isPicked) {
                    setEmployeePermissions([...employeePermissions, { id: e.target.value.id, name: e.target.value.name }]);
                }
            } 
        }

        setEmployee({
            id: 0,
            employeeData: {
                name: e.target.name === "employeeName" ? e.target.value : employee.employeeData.name,
                surname: e.target.name === "employeeSurname" ? e.target.value : employee.employeeData.surname,
                phoneNumber: "",
                email: "",
                employeeAddress: {
                    id: 0,
                    city: "",
                    postCode: "",
                    street: ""
                }
            },
            position: {
                id: 0,
                name: "",
            },
            department: {
                id: 0,
                name: "",
            },
            employeePermissions: employeePermissions,
            employmentDate: "",
            remoteWork: false
        })
    }

    const hendlePermissionDelete = chipToDelete => {
        setEmployeePermissions(chips => chips.filter(chip => chip.name !== chipToDelete.name));
    }

    const hendlePostEmployee = () => {
        PostEmployee();   
    }

    return (
        <div className={classes.mainConteiner}>          
            <form className={classes.root} noValidate autoComplete="off">
            <DarkTextField
                label="Name"
                name="employeeName"
                onChange={headleFormChange}
            />
            <DarkTextField
                label="Surname"
                name="employeeSurname"
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Department"
                name="departmentSelect"
                collection={departments}     
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Position"
                name="positionSelect"
                collection={positions}
                onChange={headleFormChange}
            />
            <DarkChipList
                label="Permissions"
                name="permissionSelect"
                collection={allPermissions}
                clipListCollection={employeePermissions}
                onChange={headleFormChange}
                onDelete={hendlePermissionDelete}
                />
                <Button variant="contained" color="primary" onClick={hendlePostEmployee}>Create</Button>
            </form>
        </div>
    );
}
export default CreateEmploy;
