            color: "#999999",
        },

        '& label.Mui-focused': {
            color: 'white',
        },
        '& .MuiInput-underline:hover:not(.Mui-disabled):before': {
            borderBottomColor: 'white',
        },
        '& .MuiInput-underline:after': {
            borderBottomColor: 'white',
        },
        '& .MuiInput-underline:before': {
            borderBottomColor: '#999999',
        },
        '& .MuiSelect-icon': {
            color: '#999999',
        },
    },
})(FormControl);

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
    },
    body: {
        backgroundColor: theme.palette.grey[400],
    },
}))(TableCell)

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '1.5rem',
        width: '100%',
    },
    container: {
        maxHeight: 540,
    },
    pagination: {
        background: theme.palette.grey[800],
        color: "white",
    },
    button: {
        background: theme.palette.grey[300],
        padding: ".4rem",
        "margin-left": ".2rem",
        "margin-right": ".2rem",
    },
    searchBox: {
        marginTop: '2.2rem',
        padding: ".1rem",
        paddingLeft:"1.8rem",
        width: '62rem',
        background: theme.palette.grey[800],  
        boxShadow:
            "0px 3px 1px -2px rgb(0 0 0 / 20%), 0px 2px 2px 0px rgb(0 0 0 / 14%), 0px 1px 5px 0px rgb(0 0 0 / 12%)",
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    whiteText: {
        color: "white",
        marginLeft: "0px",
        marginRight: "8px",

    },
}));

const EmployList = () => {  
    const classes = useStyles();
    const [employees, setEmployees] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [positions, setPositions] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(3);
    const [totalItems, setTotalItems] = useState(1);
    const [searchParams, setSearchParams] = useState({
        searchString: "",
        department: "",
        position:"",
        orderBy: 'date',
        isRemote: false
    });

    useEffect( () => {
        getEmploees(page+1, rowsPerPage);
    }, [page, rowsPerPage]); 

    useEffect(() => {
        getSearchProps()
    }, []); 

    const getEmploees = async(page,size)=>   {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(
            `http://localhost:5000/api/employee/all?page=${page}&size=${size}&order=${searchParams.orderBy}&search=${searchParams.searchString}&department=${searchParams.department}&position=${searchParams.position}`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => (setEmployees(data.items), setTotalItems(data.totalItems)));
    }

    const getSearchProps = async() => {
        const requestOptions = {
            method: 'Get',
        };
        await fetch(
            `http://localhost:5000/api/departments`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setDepartments(data));
        
        await fetch(
            `http://localhost:5000/api/positions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPositions(data));
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    const handleChangeSearchParams = event => {
        setSearchParams({
            searchParams,
            searchString: event.target.name === "searchInput" ? event.target.value : searchParams.searchString,
            department: event.target.name === "departmentSelect" ? event.target.value : searchParams.department,
            position: event.target.name === "positionSelect" ? event.target.value : searchParams.position,
            orderBy: event.target.name === "orderBy" ? event.target.value : searchParams.orderBy,
            isRemote: event.target.name === "isRemote" ? event.target.checked : searchParams.isRemote,           
        });       
    };

    const handleSearch = () => {
        getEmploees(1, rowsPerPage);
        console.log(searchParams);
    };
    return (
        <div>
            <Toolbar className={classes.searchBox}>
                <h3 className={classes.whiteText}>Employees</h3>
                <StyledTextField
                    onChange={handleChangeSearchParams}
                    label='Search...'    
                    name='searchInput'
                /> 
                <StyledSelectFC className={classes.formControl}>
                    <InputLabel id="select-params-label">Department</InputLabel>
                    <Select
                        labelId="select-params-label"
                        id="select-params"
                        name="departmentSelect"
                        onChange={handleChangeSearchParams}
                        value={0}
                    >
                        <MenuItem value={0} >All</MenuItem>
                        {departments === undefined ? <div></div> :
                                departments.map((department, index) => (
                                    <MenuItem kay={index} value={department.id}>{department.name}</MenuItem>
                                ))
                            }
                    </Select>
                </StyledSelectFC>
                <StyledSelectFC className={classes.formControl}>
                    <InputLabel id="select-params-label">Position</InputLabel>
                    <Select
                        labelId="select-params-label"
                        id="select-params"
                        name="departmentSelect"
                        onChange={handleChangeSearchParams}
                        value={0}
                    >
                        <MenuItem value={0}>All</MenuItem>
                        {positions === undefined ? <div></div> :
                            positions.map((position, index) => (
                                <MenuItem kay={index} value={position.id}>{position.name}</MenuItem>
                            ))
                        }
                    </Select>
                </StyledSelectFC>
                <StyledSelectFC className={classes.formControl}>
                    <InputLabel id="select-order-label">Order by</InputLabel>
                    <Select
                        labelId="select-order-label"
                        id="select-order"
                        name="orderBy"
                        value={searchParams.orderBy}
                        onChange={handleChangeSearchParams}
                    >
                        <MenuItem value="id">ID</MenuItem>
                        <MenuItem value="name">Name</MenuItem>
                        <MenuItem value="surname">Surname</MenuItem>
                        <MenuItem value="position">Position</MenuItem>
                        <MenuItem value="department">Department</MenuItem>
                        <MenuItem value="date">Date</MenuItem>
                    </Select>
                </StyledSelectFC>
                <FormControlLabel
                    control={
                        <Switch
                            name="isRemote"
                            checked={searchParams.remoteWork}
                            onChange={handleChangeSearchParams}
                            color="primary"
                        />
                    }
                    
                    className={classes.whiteText}
                    labelPlacement="start"
                    label="Remote Work"
                />
                <Button variant="contained" color="primary" onClick={handleSearch}>Search</Button>
            </Toolbar>

            {employees === undefined ? <div></div> :
            <Paper className={classes.root}>          
            <TableContainer className={classes.container}>
                <Table stickyHeader aria-label="sticky table" >
                    <TableHead>
                        <TableRow >
                            <StyledTableCell align="center">
                                #
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                ID
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Name
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Surname
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Email
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Position
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Department
                            </StyledTableCell>         
                            <StyledTableCell align="center">
                                Remote Work
                            </StyledTableCell>            
                            <StyledTableCell align="center">
                                Employment Date
                            </StyledTableCell>           
                            <StyledTableCell align="center">
                                Action
                            </StyledTableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody >
                        {employees.map((employee,index) => (
                            <TableRow hover key={index}>
                            <StyledTableCell align="center">
                                {index+1}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.id}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.surname}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.email}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.position.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.department.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                    {employee.remoteWork === true ?
                                        <CheckCircleIcon /> : <CloseIcon />}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employmentDate.toString().split('T')[0]}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                            <IconButton className={classes.button} size="small" aria-label="edit">
                                <VisibilityIcon/>
                            </IconButton>
                            <IconButton className={classes.button} size="small" aria-label="edit">
                                <EditIcon/>
                            </IconButton>
                            <IconButton className={classes.button}  size="small" aria-label="del">
                                <DeleteIcon/>
                            </IconButton>                                
                        </StyledTableCell>
                        </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination 
                className={classes.pagination}
                rowsPerPageOptions={[3, 10, 25, 35]}
                component="div"
                count={totalItems}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
            />
        </Paper>
        }
    </div>
    );
}
export default EmployList;
