import React, { useEffect, useState } from "react";
import { APIURL } from './GlobalComponents';
import { makeStyles } from '@material-ui/core/styles';
import { useParams } from "react-router-dom";

export const EmployeeDetails = ({ id }) => {
    const classes = useStyles();
    const [employee, setEmployee] = useState({});
    const params = useParams();

    const useStyles = makeStyles((theme) => ({
        mainConteiner: {
            marginTop: "1.8rem",
            background: theme.palette.grey[800],
            paddingBottom: '20px',

            display: "grid",
            gridTemplateColumns: "1fr 1fr 1fr",
            justifyContent: "space-between",
            alignContent: "space-between",
            gridGap: "1.2rem 1.2rem",
        },
        title: {
            color: theme.palette.text.primary,
            textAlign: 'center',
            padding: '1px',
            backgroundColor: theme.palette.primary.main,
            boxShadow: theme.shadows[2],
        },

    }));

    useEffect(() => {
        getEmploee(params.id);
    }, [])

    const getEmploee = async (empID) => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(APIURL + `employee/get/` + empID,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setEmployee(data));
    }

    return (
        <div className={classes.mainConteiner}>
            <div className={classes.title}>Employee Details</div>
            <h3>{employee.id}</h3>

        </div>
    )
}
