import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import { Link } from "react-router-dom";

import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    redirectButton: {
        margin: '8px',
        width: "10rem",
    },
    mainCard: {
        margin: '0 auto',
        marginTop: '32px',
        display: 'block',
        maxWidth: 750,
        "& p": {
            textAlign: 'justify',
            marginButton: '12px'
        }
    },
    cardMedia: {
        height: 350,
    },
}));

const StartPage = () => {
    const classes = useStyles();

    return (
        <div>
            <Card className={classes.mainCard}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                        Human Resources Manager
                    </Typography>
                    <Typography variant="body1" color="textSecondary" component="p">
                        A complex prototype application. To store employees data, and assign tasks to them.
                        Application content is depends of a employee permissions.
                        The main roles of employees are: a regular employee, team leader, and HR manager.
                        An ordinary employees can view their tasks and change their status.
                        Tasks for employees are assigned by the team leader in a dedicated view.
                        An employee who manages human resources can add new employees and change their data. It is also responsible for creating new teams.
                        All data in the application is randomly generated by a built-in generator on the backend side.
                    </Typography>
                    <Typography variant="body1" color="textSecondary" component="p">
                        To get started you need to login(button below).
                        Login data for main roles are listed below. After login, application will generate new tasks for employees.
                        This is mainly intended for presentation purposes. This is to simulate the use of applications under normal working conditions.
                    </Typography>
                </CardContent>
                <CardActions>
                    <Link style={{ textDecoration: "none" }} to="/login">
                        <Button
                            className={classes.redirectButton}
                            variant="contained"
                            color="primary"
                        >To Login Page</Button>
                    </Link>
                </CardActions>
            </Card>

        </div>
    );
}
export default StartPage;
