import React, { useEffect, useState } from "react";
import APIURL from '../../Services/Globals';
import { getCurrentUser } from '../../Services/AuthService';
import moment from "moment";
import { makeStyles } from '@material-ui/core/styles';
import { getTasks } from "../../Services/TasksService";

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { Accordion, AccordionSummary, AccordionDetails, Chip } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';


const useStyles = makeStyles((theme) => ({
    tasksContainer: {
        margin: '0 auto',

    },
    listComponent: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        color: 'white',
        boxShadow: theme.shadows[2],
        borderRadius: '3px'
    }

}));

const TasksList = () => {
    const classes = useStyles();
    const [selectedIndex, setSelectedIndex] = useState(1);
    const [tasks, setTasks] = useStylesuseState([]);


    useEffect(() => {
        getTasks(1, 10, 12).then((data) => {
            setTasks(data)
        })
    }, []);

    const handleListItemClick = (event, index) => {
        setSelectedIndex(index);
    };
    return (
        <div className={classes.tasksContainer}>
            <List component="nav" className={classes.listComponent}>
                {tasks === undefined ? null :
                    <div>
                        {tasks.map((task, index) => (
                            <ListItem
                                button
                                selected={selectedIndex === index}
                                onClick={(event) => handleListItemClick(event, index)}
                            >
                                <ListItemText primary={task.name} />
                            </ListItem>
                        ))
                        }</div>

                }
            </List>
        </div>
    );
}
export default TasksList;
