import React, { useEffect, useState } from "react";

import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

const useStyles = makeStyles({
    root: {
        marginTop: '5rem',
        width: '100%',
    },
    container: {
        maxHeight: 440,
    },
});

const EmployList = () => {  
    const classes = useStyles();
    const [employees, setEmployees] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(3);

    useEffect(() => {
        getEmploees(1,rowsPerPage)
    }, []); 

    useEffect(() => {
        getEmploees(page, rowsPerPage)
    }, [page]); 

    const getEmploees = (page,size) => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch(`http://localhost:5000/api/employee/all?page=${page}&size=${size}`, requestOptions)
            .then(response => response.json())
            .then(data => setEmployees(data.items),console.log(";o;"));
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <div>
            {employees === undefined ? <div>lol</div> :
            <Paper className={classes.root}>          
            <TableContainer className={classes.container}>
                <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <TableRow>
                            <TableCell>
                                ID
                            </TableCell>
                            <TableCell>
                                Name
                            </TableCell>
                            <TableCell>
                                Surname
                            </TableCell>
                            <TableCell>
                                Position
                            </TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {employees.map((employee,index) => (
                            <TableRow hover key={employee.i}>
                            <TableCell>
                                {employee.id}
                            </TableCell>
                            <TableCell>
                                {employee.employeeData.name}
                            </TableCell>
                            <TableCell>
                                {employee.employeeData.surname}
                            </TableCell>
                            <TableCell>
                                {employee.position.name}
                            </TableCell>
                        </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[2,3,10, 25, 100]}
                component="div"
                count={11}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
            />
        </Paper>
        }
    </div>
    );
}
export default EmployList;
