import React, { useEffect, useState } from "react";
import { APIURL } from './GlobalComponents';
import { makeStyles } from '@material-ui/core/styles';
import { useParams } from "react-router-dom";
import Chip from '@material-ui/core/Chip';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.8rem",
        background: theme.palette.grey[800],
        paddingBottom: '20px',
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    gridConteiner: {
        display: "grid",
        gridTemplateColumns: `1fr 1fr 1fr`,
        gridTemplateRows: "1fr ",
        justifyContent: "space-evenly",
        alignContent: "center",


        padding: "1rem",
        color: theme.palette.text.primary,
        '& p': {
            margin: "5px",
            display: 'inline'
        },
        '& h4': {
            margin: "5px",
            display: 'inline',
            fontSize: '16px',
        }
    },
    permitionChip: {
        margin: "2px",
        marginLeft: "5px",
        boxShadow: theme.shadows[2],

    }
}));

export const EmployeeDetails = () => {
    const classes = useStyles();
    const [employee, setEmployee] = useState({});
    const params = useParams();



    useEffect(() => {
        getEmploee(params.id);
    }, [])

    const getEmploee = async (empID) => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(APIURL + `employee/get/` + empID,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setEmployee(data));
    }

    return (
        <div className={classes.mainConteiner}>
            <div className={classes.title}>
                <h3>Employee Details:</h3>
            </div>
            {employee.person === undefined ? null :
                <div className={classes.gridConteiner}>
                    <div>
                        <h3 className={classes.header}>Personsal Info</h3>
                        <div>
                            <h4>Name: </h4>
                            <p>{employee.person.name}</p>
                        </div>
                        <div >
                            <h4>Surname: </h4>
                            <p>{employee.person.surname}</p>
                        </div>
                        <div >
                            <h4>Phone Number: </h4>
                            <p>{employee.person.phoneNumber}</p>
                        </div>
                        <div >
                            <h4>Email: </h4>
                            <p>{employee.person.email}</p>
                        </div>
                    </div>
                    <div>
                        <h3 className={classes.header}>Address:</h3>
                        <div>
                            <h4>City: </h4>
                            <p>{employee.person.employeeAddress.city}</p>
                        </div>
                        <div>
                            <h4>Street: </h4>
                            <p>{employee.person.employeeAddress.street}</p>
                        </div>
                        <div >
                            <h4>Post Code: </h4>
                            <p>{employee.person.employeeAddress.postCode}</p>
                        </div>
                    </div>

                    <div>
                        <h3 className={classes.header}>Occupation info</h3>
                        <div>
                            <h4>Employment Date: </h4>
                            <p>{employee.employmentDate.toString().split('T')[0]}</p>
                        </div>
                        <div >
                            <h4>Position: </h4>
                            <p>{employee.position.name}</p>
                        </div>
                        <div >
                            <h4>Department: </h4>
                            <p>{employee.department.name}</p>
                        </div>
                        <div >
                            <h4>Work Type: </h4>
                            <p>{employee.remoteWork === true ? "Remote" : "Office"}</p>
                        </div>
                    </div>
                    {employee.permissions === undefined ||
                        employee.permissions.length === 0 ? null :
                        <div>
                            <h3>Employee Permissions: </h3>
                            {employee.permissions.map((permition, index) => (
                                <Chip
                                    className={classes.permitionChip}
                                    key={index}
                                    label={permition.name}
                                    color="primary"
                                />

                            ))}
                        </div>
                    }
                </div>
            }
        </div>
    )
}
