import React, { useEffect, useState } from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import {APIURL, DarkTextField, DarkSelect, DarkChipList} from './GlobalComponents';

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.5rem",  
        background: theme.palette.grey[800], 
        padding:"1rem"
    },
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
}));

const CreateEmploy = () => {
    const classes = useStyles();
    const [employee, setEmployee] = useState({
        id: 0,
        employeeData: {
            name: "",
            surname: "",
            phoneNumber: "",
            email: "",
            employeeAddress: {
                id: 0,
                city: "",
                postCode: "",
                street:""
            }
        },
        position: {
            id: 0,
            name:"",
        },
        department: {
            id: 0,
            name: "",
        },
        employeePermissions:[],
        employmentDate: "",
        remoteWork: false
    });
    const [departments, setDepartments] = useState([]);
    const [positions, setPositions] = useState([]); 
    const [permissions, setPermissions] = useState([]); 
    const [employeePermissions, setEmployeePermissions] = useState([]); 

    useEffect(() => {
    getEmployeeProps()
    }, []);

    const PostEmployee = () => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "id": 0, "name": "Json", "surname": "j",
                "phoneNumber": 0, "email": "temp@mail.net",
                "EmployeeAddressId": 0,
                "employeeAddress":
                    { "id": 0, "city": "New York", "postCode": "11-111", "street": "str 11" }
            })
        };
        fetch(APIURL +'employee/build?type=linux-admin', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    const getEmployeeProps = async () => {
        const requestOptions = {
            method: 'Get',
        };
        await fetch(APIURL + `departments`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setDepartments(data));

        await fetch(APIURL + `positions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPositions(data));
        
        await fetch(APIURL + `permissions`,
            requestOptions
        )
            .then(response => response.json())
            .then(data => setPermissions(data));
    }

    const headleFormChange = event => {
        if (event.target.name === "permissionSelect") {
            if (employeePermissions.length === 0) {
                setEmployeePermissions([...employeePermissions, { id: event.target.value.id, name: event.target.value.name }]);           
            } else {
                let isPicked = false;
                employeePermissions.forEach(element => {
                    if (element.id === event.target.value.id) {
                        isPicked = true;
                    }
                });
                if (!isPicked) {
                    setEmployeePermissions([...employeePermissions, { id: event.target.value.id, name: event.target.value.name }]);
                }
            }       
        }
    }

    const hendlePermissionDelete = chip => {
        setEmployeePermissions(chips => chips.filter(chips => chips.key !== chip.key));

    }

    return (
        <div className={classes.mainConteiner}>
            <DarkTextField
                label="Name"
                name="employeeName"
                onChange={headleFormChange}
            />
            <DarkTextField
                label="Surname"
                name="employeeSurname"
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Department"
                name="departmentSelect"
                collection={departments}     
                onChange={headleFormChange}
            />
            <DarkSelect
                label="Position"
                name="positionSelect"
                collection={positions}
                onChange={headleFormChange}
            />
            <DarkChipList
                label="Permission"
                name="permissionSelect"
                collection={permissions}
                clipListCollection={employeePermissions}
                onChange={headleFormChange}
                onDelete={hendlePermissionDelete}
            />
            <form className={classes.root} noValidate autoComplete="off">
                <div>
                    <TextField required id="standard-required" label="Required" defaultValue="Hello World" />
                    <TextField disabled id="standard-disabled" label="Disabled" defaultValue="Hello World" />
                    <TextField
                        id="standard-password-input"
                        label="Password"
                        type="password"
                        autoComplete="current-password"
                    />
                    <TextField
                        id="standard-read-only-input"
                        label="Read Only"
                        defaultValue="Hello World"
                        InputProps={{
                            readOnly: true,
                        }}
                    />
                    <TextField
                        id="standard-number"
                        label="Number"
                        type="number"
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                    <TextField id="standard-search" label="Search field" type="search" />
                    <TextField
                        id="standard-helperText"
                        label="Helper text"
                        defaultValue="Default Value"
                        helperText="Some important text"
                    />
                </div>
            </form>
        </div>
    );
}
export default CreateEmploy;
