import React, { useEffect, useState } from "react";
import { makeStyles, withStyles } from '@material-ui/core/styles';
import APIURL from '../../Services/Globals';
import { getCurrentUser } from '../../Services/AuthService';
import { Link } from "react-router-dom";
import { getTasks } from "../../Services/TasksService";
import { StatBar } from "../GlobalComponents"

import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import VisibilityIcon from '@material-ui/icons/Visibility';
import CloseIcon from '@material-ui/icons/Close';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Typography from '@material-ui/core/Typography';

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
    },
    body: {
        backgroundColor: theme.palette.grey[400],
        color: "black",
    },
}))(TableCell)

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '1rem',
        width: '100%',
    },
    container: {
        maxHeight: 550,
    },
    pagination: {
        background: theme.palette.grey[800],
        color: "white",
    },
    button: {
        background: theme.palette.grey[300],
        padding: ".4rem",
        "margin-left": ".2rem",
        "margin-right": ".2rem",
        color: theme.palette.grey[800],
    },
    searchBox: {
        padding: ".1rem",
        paddingLeft: "1.8rem",
        paddingRight: "1.8rem",
        width: "max-content",
        background: theme.palette.grey[800],
        boxShadow:
            "0px 3px 1px -2px rgb(0 0 0 / 20%), 0px 2px 2px 0px rgb(0 0 0 / 14%), 0px 1px 5px 0px rgb(0 0 0 / 12%)",
    },
    whiteText: {
        color: "white",
        marginLeft: "0px",
        marginRight: "8px",
    },
    currentTask: {
        borderRadius: '4px',
        backgroundColor: "green",
        color: "white",
        boxShadow: theme.shadows[2],
    },
    noTask: {
        borderRadius: '4px',
        backgroundColor: "#bd0000",
        color: "white",
        boxShadow: theme.shadows[2],
    }
}));
const leaderID = getCurrentUser().userDetails.employeeDTO.id;
const TeamManager = () => {
    const classes = useStyles();

    const [team, setTeam] = useState({});
    const [tasksStats, setTasksStats] = useState({});
    const [currentTasks, setCurrentTasks] = useState([]);
    useEffect(() => {
        getData();
    }, []);

    const getData = () => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch(APIURL + `teams/leader/${leaderID}`, requestOptions)
            .then(response => response.json())
            .then(data => (
                setTeam(data),
                getTasksStats(data.id),
                data.members.map((member) => {
                    getTasks(1, 1, member.id, undefined, "In-Progress").then((data) => {
                        setCurrentTasks(old => [
                            ...old, data.items[0]
                        ])
                    })
                })
            ));
    }

    const getTasksStats = (teamId) => {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch(APIURL + `tasks/stats?teamid=${teamId}`, requestOptions)
            .then(response => response.json())
            .then(data => (setTasksStats(data)));

    }

    const currentTaskBar = (memberId) => {
        let boxStyle = classes.currentTask;
        let text = loadCurrrentTask(memberId);
        if (text === undefined) {
            boxStyle = classes.noTask;
            text = "No Task Assigned"
        }
        return (
            <Typography variant="body1" className={boxStyle}>
                {text}
            </Typography>
        );
    }
    const loadCurrrentTask = (id) => {
        let currTask;
        currentTasks.map(task => {
            if (task !== undefined) {
                if (task.assignedEmployeeId === id) {
                    currTask = task.name;
                }
            }
        });
        return currTask;
    }
    return (
        <div>
            {tasksStats === undefined ? null :
                <Card>
                    <StatBar
                        valueMax={10}
                        valueCurrent={2}
                        text="Requested"
                        bcolor='rgb(231, 170, 35)'
                    />
                    <StatBar
                        valueMax={10}
                        valueCurrent={2}
                        text="In-Progress"
                        bcolor='rgb(56, 81, 216)'
                    />
                    <StatBar
                        valueMax={10}
                        valueCurrent={2}
                        text="Completed"
                        bcolor='rgb(0, 158, 7)'
                    />
                </Card>
            }
            {team.members === undefined ? <div></div> :
                <Card className={classes.root}>
                    <CardHeader
                        title={'"' + team.name + '"' + " Team Members:"}
                    />

                    <TableContainer className={classes.container}>
                        <Table stickyHeader aria-label="sticky table" >
                            <TableHead>
                                <TableRow >
                                    <StyledTableCell align="center">
                                        #
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        ID
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Name
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Surname
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Email
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Position
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Remote Work
                                    </StyledTableCell>
                                    <StyledTableCell align="center">
                                        Now Working On
                                    </StyledTableCell>
                                    <StyledTableCell>
                                    </StyledTableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody >
                                {team.members.map((employee, index) => (
                                    <TableRow hover key={employee.id}>
                                        <StyledTableCell align="center">
                                            {index + 1}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.id}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.person.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.person.surname}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.person.email}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.position.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            {employee.remoteWork === true ?
                                                <CheckCircleIcon /> : <CloseIcon />}
                                        </StyledTableCell>
                                        <StyledTableCell align="center">

                                            {currentTasks.length !== team.members.length ? null :
                                                currentTaskBar(employee.id)
                                            }

                                        </StyledTableCell>
                                        <StyledTableCell align="center">
                                            <Link to={{ pathname: `/main/employee-details/${employee.id}` }}>
                                                <IconButton className={classes.button} size="small" aria-label="view">
                                                    <VisibilityIcon />
                                                </IconButton>
                                            </Link>
                                        </StyledTableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Card>
            }
        </div>
    );
}
export default TeamManager;
