import React, { useEffect, useState } from "react";

import { fade, makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import InputBase from '@material-ui/core/InputBase';

import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Toolbar from '@material-ui/core/Toolbar';
import Card from '@material-ui/core/Card';

import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';
import CloseIcon from '@material-ui/icons/Close';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import SearchIcon from '@material-ui/icons/Search';

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
    },
    body: {
        backgroundColor: theme.palette.grey[400],
    },
}))(TableCell)

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '1.5rem',
        width: '100%',
    },
    container: {
        maxHeight: 540,
    },
    pagination: {
        background: theme.palette.grey[800],
        color: "white",
    },
    button: {
        background: theme.palette.grey[300],
        padding: ".4rem",
        "margin-left": ".2rem",
        "margin-right": ".2rem",
    },
    searchBox: {
        borderRadius:"3rem",
        marginTop: '2.2rem',
        padding: ".3rem",
        width: 'auto',
        background: theme.palette.grey[800],  
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));

const EmployList = () => {  
    const classes = useStyles();
    const [employees, setEmployees] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(3);
    const [totalItems, setTotalItems] = useState(1);

    useEffect( () => {
        getEmploees(page+1, rowsPerPage);
    }, [page, rowsPerPage]); 

    const getEmploees = async(page,size)=>   {
        const requestOptions = {
            method: 'Get',
            headers: { 'Content-Type': 'application/json' }
        };
        await fetch(`http://localhost:5000/api/employee/all?page=${page}&size=${size}`, requestOptions)
            .then(response => response.json())
            .then(data => (setEmployees(data.items), setTotalItems(data.totalItems)));
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <div>
            <Toolbar className={classes.searchBox}>
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <InputBase
                        placeholder="Searchâ€¦"
                        classes={{
                            root: classes.inputRoot,
                            input: classes.inputInput,
                        }}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </div>
            </Toolbar>

            {employees === undefined ? <div>lol</div> :
            <Paper className={classes.root}>          
            <TableContainer className={classes.container}>
                <Table stickyHeader aria-label="sticky table" >
                    <TableHead>
                        <TableRow >
                            <StyledTableCell align="center">
                                #
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                ID
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Name
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Surname
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Email
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Position
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                Department
                            </StyledTableCell>         
                            <StyledTableCell align="center">
                                Remote Work
                            </StyledTableCell>            
                            <StyledTableCell align="center">
                                Employment Date
                            </StyledTableCell>           
                            <StyledTableCell align="center">
                                Action
                            </StyledTableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody >
                        {employees.map((employee,index) => (
                            <TableRow hover key={employee.id}>
                            <StyledTableCell align="center">
                                {index+1}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.id}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.surname}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employeeData.email}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.position.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.department.name}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                    {employee.remoteWork === true ?
                                        <CheckCircleIcon /> : <CloseIcon />}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                                {employee.employmentDate.toString().split('T')[0]}
                            </StyledTableCell>
                            <StyledTableCell align="center">
                            <IconButton className={classes.button} size="small" aria-label="edit">
                                <VisibilityIcon/>
                            </IconButton>
                            <IconButton className={classes.button} size="small" aria-label="edit">
                                <EditIcon/>
                            </IconButton>
                            <IconButton className={classes.button}  size="small" aria-label="del">
                                <DeleteIcon/>
                            </IconButton>                                
                        </StyledTableCell>
                        </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination 
                className={classes.pagination}
                rowsPerPageOptions={[3, 10, 25, 35]}
                component="div"
                count={totalItems}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
            />
        </Paper>
        }
    </div>
    );
}
export default EmployList;
