import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField, DarkSelect } from '../GlobalComponents';
import APIURL from '../../Services/Globals'
import { getCurrentUser } from '../../Services/AuthService';

import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';

function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.8rem",
        background: theme.palette.grey[800],
        paddingBottom: '20px'
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    formGrid: {
        display: "grid",
        gridTemplateColumns: "1fr",
        justifyContent: "space-between",
        alignContent: "space-between",
    },
}));

const Profile = () => {
    const classes = useStyles();
    const [user, setUser] = useState(getCurrentUser().userDetails);
    const [changePassword, setChangePassword] = useState({
        isChangePassword: false,
        old: '',
        new: ''
    });

    return (
        <div className={classes.mainConteiner}>
            <div boxshadow={2} className={classes.title}>
                <h3 >User Credentials</h3>
            </div>
            <form noValidate autoComplete="off">
                <div className={classes.formGrid}>
                    <DarkTextField
                        label="Username"
                        value={user.username}
                        onChange={(e) => setUser({ ...user, username: e.target.value })}
                    />
                    <DarkTextField
                        label="Password"
                        type={'password'}
                        value={user.password}
                        onChange={(e) => setUser({ ...user, password: e.target.value })}
                    />
                    {!changePassword.isChangePassword ?
                        <Button
                            className={classes.createButton}
                            variant="contained"
                            color="primary"
                            onClick={() => setChangePassword({ ...changePassword, isChangePassword: true })}
                        >Change Password</Button> :
                        <React.Fragment>
                            <DarkTextField
                                label="Old Password"
                                type={'password'}
                                value={changePassword.old}
                                onChange={(e) =>
                                    setChangePassword({ ...changePassword, old: e.target.value })
                                }
                            />
                            <DarkTextField
                                label="New Password"
                                type={'password'}
                                value={changePassword.new}
                                onChange={(e) =>
                                    setChangePassword({ ...changePassword, new: e.target.value })
                                }
                            />
                            <Button
                                className={classes.createButton}
                                variant="contained"
                                color="primary"
                                onClick={() => console.log(changePassword)}
                            >Confirm</Button>
                        </React.Fragment>
                    }

                </div>
            </form>
        </div>
    );
}
export default Profile;
