import {makeStyles, withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';

 const StyledTextField = withStyles({
     root: {

            '& .MuiInputBase-root': {
                color: "white",
                marginRight: "10px",
                marginLeft: "20px",
                marginBottom: "11px",
            },
            
            '& .MuiFormLabel-root': {
                color: "#999999",
                marginLeft: "20px",
            },
            
            '& label.Mui-focused': {
                color: 'white',
            },
            '& .MuiInput-underline:hover:not(.Mui-disabled):before': {
                borderBottomColor: 'white',
            },
            '& .MuiInput-underline:after': {
                borderBottomColor: 'white',
            },
            '& .MuiInput-underline:before': {
                borderBottomColor: '#999999',
            },
        },
 })(TextField);

const StyledSelectFC = withStyles({
    root: {
        '& .MuiInputBase-root': {
            color: "white",
            marginRight: "8px",
            marginBottom: "11px",
        },

        '& .MuiFormLabel-root': {
            color: "#999999",
        },

        '& label.Mui-focused': {
            color: 'white',
        },
        '& .MuiInput-underline:hover:not(.Mui-disabled):before': {
            borderBottomColor: 'white',
        },
        '& .MuiInput-underline:after': {
            borderBottomColor: 'white',
        },
        '& .MuiInput-underline:before': {
            borderBottomColor: '#999999',
        },
        '& .MuiSelect-icon': {
            color: '#999999',
        },
    },
})(FormControl);
const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
}));

export const DarkTextField = ({ onChange, label, name }) => {
    return (
        <StyledTextField
            onChange={onChange}
            label={label}
            name={name}
        />
    );
};
export default DarkTextField;

export const DarkSelect = ({ onChange, label, name, value, firstVal, firstLabel, collection }) => {
    const classes = useStyles();
    return (
        <StyledSelectFC className={classes.formControl}>
            <InputLabel id="select-params-label">{label}</InputLabel>
            <Select
                labelId="select-params-label"
                id="select-params"
                name={name}
                onChange={onChange}
                value={value}
            >
                {
                    firstVal === undefined ? <di></di> :
                        <MenuItem value={firstVal} >{firstLabel}</MenuItem>
                }
                {collection === undefined ? <div></div> :
                    collection.map((item, index) => (
                        <MenuItem kay={index} value={item.id}>{item.name}</MenuItem>
                    ))
                }
            </Select>
        </StyledSelectFC>
    );
};
