import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { DarkTextField, DarkSelect, DarkChipList } from '../GlobalComponents';
import APIURL from '../../Services/Globals'

import Button from '@material-ui/core/Button';
import { KeyboardDatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import { format } from 'date-fns'

const useStyles = makeStyles((theme) => ({
    mainConteiner: {
        marginTop: "1.8rem",
        background: theme.palette.grey[800],
        paddingBottom: '20px'
    },
    title: {
        color: theme.palette.text.primary,
        textAlign: 'center',
        padding: '1px',
        backgroundColor: theme.palette.primary.main,
        boxShadow: theme.shadows[2],
    },
    formGrid: {
        display: "grid",
        gridTemplateColumns: "1fr 1fr 1fr",
        justifyContent: "space-between",
        alignContent: "space-between",
        gridGap: "1.2rem 1.2rem",
    },
    datePicker: {
        marginTop: '0',
        marginLeft: "20px"
    },
    createButton: {
        margin: '0 auto',
        display: 'block',
        width: "19rem",
    }
}));

const CreateTask = () => {
    const classes = useStyles();
    const [task, setTask] = useState({})
    const [allEmployees, setAllEmployees] = useState([{ id: "tt", name: "rr" }])
    const [employeesToAssign, setEmployeesToAssign] = useState([])



    const headleFormChange = e => {

    }

    const headleDateChange = (e) => {
        // setEmployee({ ...employee, employmentDate: e })
    }
    const hendlePermissionDelete = chipToDelete => {
        // setEmployeePermissions(chips => chips.filter(chip => chip.name !== chipToDelete.name));
    }

    const hendlePostTask = () => {

        // PostEmployee(employee);
    }

    return (
        <div className={classes.mainConteiner}>
            <div boxshadow={2} className={classes.title}>
                <h3 >Create New Task</h3>
            </div>
            <form className={classes.root} noValidate autoComplete="off">
                <div className={classes.formGrid}>
                    <DarkTextField
                        label=" Task Name"
                        name="taskName"
                        onChange={headleFormChange}
                    />
                    <DarkTextField
                        label="Description"
                        maxRows={5}
                        name="taskDescription"
                        onChange={headleFormChange}
                    />
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <KeyboardDatePicker
                            className={classes.datePicker}
                            disableToolbar
                            name="dateStart"
                            variant="inline"
                            format="yyyy-MM-dd"
                            margin="normal"
                            id="date-picker-inline"
                            label="Start Time"
                            value={task.startTime}
                            onChange={headleDateChange}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                    </MuiPickersUtilsProvider>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <KeyboardDatePicker
                            className={classes.datePicker}
                            disableToolbar
                            name="dateDeadline"
                            variant="inline"
                            format="yyyy-MM-dd"
                            margin="normal"
                            id="date-picker-inline"
                            label="Deadline"
                            value={task.Deadline}
                            onChange={headleDateChange}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                    </MuiPickersUtilsProvider>
                </div>
                <DarkChipList
                    label="Employees"
                    name="employeeSelect"
                    collection={allEmployees}
                    clipListCollection={employeesToAssign}
                    onChange={headleFormChange}
                    onDelete={hendlePermissionDelete}
                />
                <Button
                    className={classes.createButton}
                    variant="contained"
                    color="primary"
                    onClick={hendlePostTask}
                >Create</Button>
            </form>
        </div>
    );
}
export default CreateTask;
